{"operationName":"files","variables":{"owner":"owner","name":"repo","expression":"master:content/posts"},"query":"query files($owner: String!, $name: String!, $expression: String!) {
  repository(owner: $owner, name: $name) {
    ...RepositoryParts
    object(expression: $expression) {
      ...ObjectParts
      ... on Tree {
        entries {
          ...FileEntryParts
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment RepositoryParts on Repository {
  id
  isFork
  __typename
}

fragment ObjectParts on GitObject {
  id
  sha: oid
  __typename
}

fragment FileEntryParts on TreeEntry {
  name
  sha: oid
  type
  blob: object {
    ... on Blob {
      size: byteSize
      __typename
    }
    __typename
  }
  __typename
}
"}

